INSERT INTO book VALUES(156,'Blindsight','2006','en','','В 2082 году человечество убедилось, что оно не одиноко во Вселенной. Бесчисленные зонды светя­щейся паутиной окутали Землю. На установление контакта с внеземной цивилизацией направлен ко­рабль «Тезей», несущий на борту наспех собранную команду специалистов. Но, по достижении цели, ис­следователям предстоит понять, что самые невероят­ные фантазии об инопланетном разуме меркнут по сравнению с реальностью, и на кон поставлена судь­ба Земли и всего человечества.');
INSERT INTO book VALUES(157,'Маска','2012','uk','',replace('авантюрний історичний роман\nПодії відбуваються на початку 19-го століття на Волині. Книга, як на мене, трохи затягнута\n-------------\nДія цього авантюрного історичного роману відбувається на початку XІX століття у багатьох країнах Європи. Він про загадкову "людину в п''яти масках". Цей грабіжник виринає нізвідки й зникає в нікуди. Він з''являється на зібраннях аристократів Польщі, України, Німеччини, Трансільванії, наводить жах на присутніх та здійснює добре сплановані зухвалі пограбування. Аж раптом якийсь сміливець зриває з нього маску..','\n',char(10)));
