INSERT INTO book VALUES(160,'«Аляска»','2017','uk','','До психлікарні його привезли без тями і… в гідрокостюмі. Перед цим чоловік забіг до церкви, наляканий чимось несусвітнім, кимось із потойбічного світу. Покликаний рятувати людей, тепер він сам потребував допомоги, і в першу чергу — душевної. Це дивно й безглуздо, та в психушці пацієнт знайде хоч і тимчасовий, проте надійний прихисток від проблем і від самого себе. Але попереду — ще неймовірніше безглуздя…');
