INSERT INTO book VALUES(198,'Понаїхали','2015','uk','',replace('Україна, місто Білий Сад (Слов’янськ), початок 2000-х, сімʼя Ткачуків: Юрій, Ольга і їхні сини Сергій і Володя.\nЮрій їде на заробітки в США, Ольга їде в Італію, Сергій їде в Москву.\nТакож про мігрантів (зі сходу) в Україні\nПро заробітчан і розбиті сімʼї.','\n',char(10)));
