INSERT INTO book VALUES(200,'Візит доктора Фройда','2016','uk','',replace('роман з циклу пригод львівського комісара Адама Вістовича\nВ 1904 році у Відні, а згодом і у Львові стається серія жорстоких і зухвалих убивств. Розслідування Вістовича перетворюється, на безкомпромісну дуель між ним та вбивцею, якого преса нарекла Віденським Упирем.\nНесподівано свою допомогу комісарові пропонує вже славетний на той час віденський психоаналітик Зиґмунд Фройд.','\n',char(10)));
