INSERT INTO book VALUES(284,'Гра у три руки','2016','uk','',replace('(досить сумбурний детектив)\nІрина досягла всього сама, але дорогою втратила важливі людські якості. На новий рік вона випадковою СМСкою врятувала життя чоловіку (Олегу). Потім спілкуючись з Олегом через інтернет, закохалася. Кохання пройшло, коли взнала, що він інвалід, прикутий до візка...','\n',char(10)));
