INSERT INTO book VALUES(1802,'Origin Story: A Big History of Everything','2018','en','',replace('Девід Крістіан є одним з теоретиків комплексної дисципліни – Великої історії, яка пояснює виникнення всього за допомогою фізики, біології, археології, соціології, економіки та інших наук.\nУ цій книжці автор описує процеси зародження й розвитку космосу, виникнення цивілізацій і місце людини у світі.','\n',char(10)));
