INSERT INTO book VALUES(1808,'Die Vollkommenen - Perfektes Design','2022','de','',replace('Philine, Leti - сестри (без генетичного редагування), їхня мати вмирає в лікарні. Батько Stefan - власник лікарської практики з планування і редагування геному дітей, має чотири патенти з редагування.\nVeit - молодий практикант в Штефана.\nЛюди прагнуть редагувати геном дітей, щоб дати їм більше шансів.\nВсі люди мають імплантований ID-Chip\nPhiline вбиває батька, в потім Leti випадково вбиває Philine.','\n',char(10)));
