PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "author_names" (
	"author_id"	INTEGER NOT NULL,
	"name"	TEXT NOT NULL,
	"lang"	TEXT,
	"type"	TEXT,
	PRIMARY KEY("author_id","name")
);
CREATE TABLE IF NOT EXISTS "author_book" (
	"author_id"	INTEGER NOT NULL,
	"book_id"	INTEGER NOT NULL,
	PRIMARY KEY("author_id","book_id")
);
CREATE TABLE IF NOT EXISTS "author" (
	"id"	INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,
	"name"	TEXT NOT NULL UNIQUE,
	"lang"	TEXT,
	"note"	TEXT
);
CREATE TABLE IF NOT EXISTS "book_names" (
	"id"	INTEGER NOT NULL,
	"book_id"	INTEGER NOT NULL,
	"name"	TEXT NOT NULL,
	"lang"	TEXT NOT NULL,
	"goodreads_id"	TEXT,
	"lib_file"	TEXT,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "book_readed" (
	"id"	INTEGER NOT NULL,
	"book_id"	INTEGER NOT NULL,
	"date_read"	TEXT,
	"lang_read"	TEXT NOT NULL,
	"medium"	TEXT,
	"score"	INTEGER,
	"title_read"	TEXT,
	"note"	TEXT,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "tag" (
	"id"	INTEGER NOT NULL,
	"name_en"	TEXT NOT NULL UNIQUE,
	"name_uk"	TEXT NOT NULL UNIQUE,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "book_tag" (
	"book_id"	INTEGER NOT NULL,
	"tag_id"	INTEGER NOT NULL,
	PRIMARY KEY("book_id","tag_id")
);
CREATE TABLE IF NOT EXISTS "book" (
	"id"	INTEGER NOT NULL,
	"title"	TEXT NOT NULL,
	"publish_date"	BLOB,
	"lang"	TEXT,
	"series"	TEXT,
	"note"	TEXT,
	PRIMARY KEY("id" AUTOINCREMENT)
);
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "book_name_uk" ON "book_names" (
	"book_id",
	"lang",
	"name"
);
COMMIT;
